import { toast } from "react-toastify";

const CustomToast = ({ boolsuccess, msg }) => {
  toast.dismiss();
  console.log(boolsuccess);
  console.log(msg);
  if (boolsuccess) {
    toast.success(msg);
  } else if (msg) {
    toast.error(msg);
  }
};

export default CustomToast;
