import { toast } from "react-toastify";

const CustomToast = ({ boolsuccess, errMsg }) => {
  toast.dismiss();
  if (boolsuccess) {
    toast.success(onSuccessLoginNavigate);
  } else if (errMsg) {
    toast.error(errMsg);
  } else {
    toast.error("error");
  }
};

export default CustomToast;
