import { BiCalendar } from "react-icons/bi";
import AddAppointment from "./components/AddAppointment";
import { AppointmentInfo } from "./components/AppointmentInfo";
import Search from "./components/Search";
import { useState, useEffect, useCallback } from "react";
import {
  deleteAptmnt,
  getAppointments,
  saveAppointment,
} from "./appointment/helper/appointmentApi";

function App() {
  const [appointmentList, setAppointmentList] = useState([]);
  const [query, setQuery] = useState("");
  const [sortBy, setSortBy] = useState("petName");
  const [orderBy, setOrderBy] = useState("asc");

  let filteredAppointmentList = appointmentList
    .filter((item) => {
      return (
        item.petName.toLowerCase().includes(query.toLowerCase()) ||
        item.ownerName.toLowerCase().includes(query.toLowerCase()) ||
        item.aptNotes.toLowerCase().includes(query.toLowerCase())
      );
    })
    .sort((a, b) => {
      let order = orderBy === "asc" ? 1 : -1;
      return a[sortBy].toLowerCase() < b[sortBy].toLowerCase()
        ? -1 * order
        : 1 * order;
    });

  const fetchData = useCallback(() => {
    getAppointments().then((data) => {
      console.log(data);
      if (!data) {
        setAppointmentList([]);
        console.log("data");
      } else {
        setAppointmentList(data);
      }
    });
  }, []);

  useEffect(() => {
    fetchData();
    // return () => {
    //   cleanup;
    // };
  }, [fetchData]);

  /// Other methods
  const deleteAppointment = async (appointmentId) => {
    await deleteAptmnt().then((data) => {
      fetchData();
    });
    setAppointmentList(
      appointmentList.filter((appointment) => {
        return appointment._id !== appointmentId;
      })
    );
  };

  const onQueryChange = (myQuery) => {
    setQuery(myQuery);
  };

  const saveApt = async (myAppointment) => {
    await saveAppointment(myAppointment).then((data) => {
      // if (data.msg === "success") {
      // setAppointmentList([]);
      console.log("here");
      console.log(myAppointment);
      fetchData();
      // }
    });
  };

  /// Main UI method

  return (
    <div className="App container mx-auto mt-3 font-thin">
      <div className="mx-4">
        <h1 className="text-5xl mb-3 flex justify-center">
          <BiCalendar className="inline-block align-top text-red-400" />
          Your Pet Appointments.
        </h1>
        <AddAppointment
          onSendAppointment={(myAppointment) =>
            // setAppointmentList([...appointmentList, myAppointment])
            saveApt(myAppointment)
          }
          // lastId={appointmentList.reduce(
          //   (max, item) => (Number(item._id) > max ? Number(item._id) : max),
          //   0
          // )}
        />
        <Search
          query={query}
          onQueryChange={onQueryChange}
          orderBy={orderBy}
          onOrderByChange={(mySort) => setOrderBy(mySort)}
          sortBy={sortBy}
          onSortByChange={(mySort) => setSortBy(mySort)}
        />

        <ul className="divide-y divide-gray-200">
          {filteredAppointmentList.length > 0
            ? filteredAppointmentList.map((appointment) => (
                <AppointmentInfo
                  key={appointment._id}
                  appointment={appointment}
                  deleteAppointment={deleteAppointment}
                />
              ))
            : "No Data Found"}
        </ul>
      </div>
    </div>
  );
}

export default App;
