import { toast } from "react-toastify";

const CustomToast = ({ boolsuccess, msg }) => {
  toast.dismiss();
  if (boolsuccess) {
    toast.success(msg);
  } else if (msg) {
    toast.error(msg);
  } else {
    toast.error("error");
  }
};

export default CustomToast;
