import { toast } from "react-toastify";

export const CustomToast = ({ boolsuccess, msg }) => {
  toast.dismiss();
  console.log(boolsuccess);
  console.log(msg);
  if (boolsuccess) {
    return toast.success(msg);
  } else if (msg) {
    return toast.error(msg);
  }
  return;
};
